import axios from "axios";
import { useEffect, useState } from "react";
import './custom'
import "./App.css";

function App() {
  const [users, setUseres] = useState([]);
  const [text, setText] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    const loadUsers = async () => {
      const response = await axios.get("https://reqres.in/api/users");
      setUseres(response.data.data);
    };
    loadUsers();
  }, []);

  const onChangeHandler = (text) => {
    let matchs = []
    if(text.length > 0){
      matchs = users.filter(user =>{
        const regex = new RegExp(`${text}`, "gi")
        return user.email.match(regex)
      })
    }
    console.log('matchs', matchs);
    setSuggestions(matchs)

    setText(text);

  };

  return (
    <div className="container">

      <input className="col-md-12 input" style={{marginTop:20}}
        type="text"
        onChange={(e) => onChangeHandler(e.target.value)}
        value={text}
      />
      {suggestions && suggestions.map((suggestion, i) =>
      <div key={i} className="suggetion col-md-12 justify-content-md-center"> {suggestion.email} </div>
      )}
    </div>
  );
}

export default App;
